import React, {useState, useEffect} from 'react';
import { useRouteMatch } from "react-router-dom";
import { Container, Row, Card, Form, Button, Image } from 'react-bootstrap';
import firebase from 'firebase';

const initialFormState = {
  isValid: false,
  loading: true,
  values: {
    name: '',
    email: '',
    password: '',
    passwordConfirmation: '',
    bio: '',
    avatar: ''
  },
  errors: {
    name: '',
    email: '',
    password: '',
    passwordConfirmation: '',
    bio: '',
    avatar: ''
  },
  touched: {
    name: '',
    email: '',
    password: '',
    passwordConfirmation: '',
    bio: '',
    avatar: ''
  }
}

const EditUserForm = (props) => {
  const routerMatch = useRouteMatch("/user/edit/:id");
  const id = routerMatch.params.id;
  const [fileName, setFileName] = useState("Insira um arquivo válido");
  const database = firebase.database();

  const [formState, setFormState] = useState(initialFormState);

  const queryUserByID = () => {
    firebase.database().ref('Users').child(`${id}`).once('value', (snapshot) => {
      setFormState(formState => ({
        ...formState,
        loading: false,
        values: snapshot.val()
      }))
    });
  }

  if(formState.loading) queryUserByID();

  console.log(formState.values);

  const handleAvatarImage = () => {
    /*if()
    bsCustomFileInput.init()

    var btn = document.getElementById('btnResetForm')
    var form = document.querySelector('form')
    btn.addEventListener('click', function () {
      form.reset()
    })*/
  }

  return (
    <Container>

      <Card>
        <Card.Header  className="bg-dark text-white" border="warning" as="h4">Editar</Card.Header>
        <Card.Body>
          <Form>

            <Form.Group controlId="formBasicAvatar">
              <Form.Label>Avatar Personalizado</Form.Label>

              <Image /*src={handleAvatarImage}*/ roundedCircle />

              <Form.File 
                id="inputGroupFile01"
                label={fileName}
                value={formState.values.avatar}
                data-browse="Insira uma imagem"
                onChange={(e) => setFileName(e.target.files[0].name)}
                custom                
              />

              <Form.Text className="text-muted">
                Formatos válidos: PNG, JPEG, JPG.
              </Form.Text>
            </Form.Group>   

            <Form.Group controlId="formBasicBio">
              <Form.Label>Descrição do seu perfil</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Descrição" 
                value={formState.values.bio}
              />
            </Form.Group>

            <Form.Group controlId="formBasicName">
              <Form.Label>Nome</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Nome Sobrenome"             
                value={formState.values.name}
             />
            </Form.Group>

            <Form.Group controlId="formBasicPassword">
              <Form.Label>Senha</Form.Label>
              <Form.Control 
                type="password" 
                placeholder="Senha" 
                value={formState.values.password}
              />
            </Form.Group>

            <Form.Group controlId="formBasicPasswordConfirmation">
              <Form.Label>Confirmar Senha</Form.Label>
              <Form.Control 
                type="password" 
                placeholder="Senha" 
                value={formState.values.passwordConfirmation}
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Atualizar
            </Button>

          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
}

export default EditUserForm;
